import PropTypes from 'prop-types';
import Popup from "reactjs-popup";
import 'semantic-ui-css/semantic.min.css';
import '../style/web.css';
import { Icon } from 'semantic-ui-react';


class Discussion extends Component{
    constructor(props) {
            super(props)
