import React,{Component} from 'react';
import { Link } from 'react-router-dom';

class Login extends Component {
    constructor(props) {
        super(props);
        this.handleEmailChange = this.handleEmailChange.bind(this);
        this.handlePasswordChange = this.handlePasswordChange.bind(this);
        this.login = this.login.bind(this);
        this.state = {
          email:'',
          password:''
        };
    }
    render() {
        return (
        <div>
          <form className="form-signin">
                <h2 className="form-signin-heading"> Please sign in </h2>
                <label for="inputEmail" className="sr-only">Email address</label>
                <input type="email" onChange={this.handleEmailChange} id="inputEmail" className="form-control" placeholder="Email address" required autofocus />
                <label for="inputPassword" className="sr-only">Password</label>
                <input type="password" onChange={this.handlePasswordChange} id="inputPassword" className="form-control" placeholder="Password" required />
                <button className="btn btn-lg btn-primary btn-block" onClick={this.login} type="button">Log in</button>              
            </form>
            <div>
            <Link to="/signup">Sign up</Link>
           </div>
        </div>
        )
    }

    getConnect(){  
        let text = {user:this.state.email,password:this.state.password} 
        let send = JSON.stringify(text);   
        fetch(`http://127.0.0.1:8081/password`,{   
            method: 'POST',
            headers: {'Content-Type': 'application/json; charset=utf-8'},
            body: send
        }).then(res => res.json()).then(
            data => {
                if(data.success) window.alert('successful');
                else window.alert('fail')
            }
        )
    }

    handleEmailChange(e){
        this.setState({email:e.target.value})
    }
    handlePasswordChange(e){
        this.setState({password:e.target.value})
    }

    handleClick(event){
        this.props.history.push('/signup');
    }

    login(){
        this.getConnect();
       // alert('Email address is ' + this.state.email + ' Password is ' + this.state.password);            
    }

}



export default Login;
