            username: '',
            showPreviewModal: false,
            showCropModal: false,
            imgFile: '',
            imgSrc: ''
         };
    }



    render(){
        const uploadButton = (
            <div className="upload-plus-text">
              <i type="plus" />
              <div className="ant-upload-text">上传照片</div>
            </div>
          );
        const imagePreview = (
            <div
              className="modal">

              <div className="preview-image">
                <span className="preview-image-btn">
                  <a title="预览图片" href="javascript:;">
                    <i
                      type="eye"
                      className="icon-btn"
                      onClick={this.handlePreview}
                    />
                  </a>
                  <a title="删除图片" href="javascript:;">
                    <i
                      type="delete"
                      className="icon-btn"
                      onClick={this.swapImageFile}
                    />
                  </a>
                </span>
              </div>
            </div>
          );
        return (
            <div>  
            <Menu>
            <Menu.Item>
                <Link to="/Home/" style={{color:'black'}}>
                    <div>Home</div>
                </Link>
            </Menu.Item>
            <Menu.Item>
                <Link to="/Honorroll/" style={{color:'black'}}>
                    <div>Honor roll</div>
                </Link>
            </Menu.Item>
            {/* <Menu.Item>
                {this.changeContent()}
            </Menu.Item> */}
            </Menu>



<div>
    <input
      type="file"
      id={this.state.imgSrc ? '' : 'file'}
      accept="image/jpeg, image/gif, image/png, image/bmp"
      className="input-file"
      onChange={e => {
        this.onFileChange(e);
      }}
    />
    <label htmlFor="file" className="ant-upload">
      {this.state.imgSrc ? imagePreview : uploadButton}
    </label>
</div>
<button type="button" class="btn btn-primary" data-toggle="modal" data-target="#exampleModal">
  Launch demo modal
</button>

<div class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
